-spec test_character_escape_case_1(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_character_escape_case_1(_Config) ->
    ?assertMatch(
        {ok, <<"<p>!&quot;#$%&amp;'()*+,-./:;&lt;=&gt;?@[\\]^_`{|}~</p>"/utf8>>},
        markdown:to_html(
            <<"\\!\\\"\\#\\$\\%\\&\\'\\(\\)\\*\\+\\,\\-\\.\\/\\:\\;\\<\\=\\>\\?\\@\\[\\\\\\]\\^\\_\\`\\{\\|\\}\\~"/utf8>>
        ),
        "should support escaped ascii punctuation"
    ),
    ok.

-spec test_character_escape_case_2(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_character_escape_case_2(_Config) ->
    ?assertMatch(
        {ok, <<"<p>\\→\\A\\a\\ \\3\\φ\\«</p>"/utf8>>},
        markdown:to_html(<<"\\→\\A\\a\\ \\3\\φ\\«"/utf8>>),
        "should not support other characters after a backslash"
    ),
    ok.

-spec test_character_escape_case_3(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_character_escape_case_3(_Config) ->
    ?assertMatch(
        {ok, <<"""
        <p>*not emphasized*
        &lt;br/&gt; not a tag
        [not a link](/foo)
        `not code`
        1. not a list
        * not a list
        # not a heading
        [foo]: /url &quot;not a reference&quot;
        &amp;ouml; not a character entity</p>
        """/utf8>>},
        markdown:to_html(<<"""
        \*not emphasized*
        \<br/> not a tag
        \[not a link](/foo)
        \`not code`
        1\. not a list
        \* not a list
        \# not a heading
        \[foo]: /url "not a reference"
        \&ouml; not a character entity
        """/utf8>>),
        "should escape other constructs"
    ),
    ok.

