-spec test_definition_case_1(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_definition_case_1(_Config) ->
    ?assertMatch(
        {ok, <<"<p><a href=\"/url\" title=\"title\">foo</a></p>"/utf8>>},
        markdown:to_html(<<"[foo]: /url \"title\"\n\n[foo]"/utf8>>),
        "should support link definitions"
    ),
    ok.

-spec test_definition_case_2(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_definition_case_2(_Config) ->
    ?assertMatch(
        {ok, <<"<p>[foo]:</p>\n<p>/url</p>\n<p>[foo]</p>"/utf8>>},
        markdown:to_html(<<"[foo]:\n\n/url\n\n[foo]"/utf8>>),
        "should not support blank lines before destination"
    ),
    ok.

-spec test_definition_case_3(Config) -> markdown_test:testcase() when
