                                        position = {some, markdown_unist_position:new(1, 7, 6, 2, 6, 10)}
                                    })
                                ]),
                                position = {some, markdown_unist_position:new(1, 7, 6, 2, 6, 10)}
                            })
                        ]),
                        identifier = <<"a">>,
                        label = {some, <<"a">>},
                        position = {some, markdown_unist_position:new(1, 1, 0, 3, 1, 11)}
