-spec test_html_text_case_1(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_html_text_case_1(_Config) ->
    ?assertMatch(
        {ok, <<"<p>a &lt;b&gt; c</p>"/utf8>>},
        markdown:to_html(<<"a <b> c"/utf8>>),
        "should encode dangerous html by default"
    ),
    ok.

-spec test_html_text_case_2(Config) -> markdown_test:testcase() when
