-spec test_math_flow_case_1(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_math_flow_case_1(_Config) ->
    ?assertMatch(
        {ok, <<"<p>$$\na\n$$</p>"/utf8>>},
        markdown:to_html(<<"$$\na\n$$"/utf8>>),
        "should not support math (flow) by default"
