-spec test_math_text_case_1(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_math_text_case_1(_Config) ->
    ?assertMatch(
        {ok, <<"<p>$a$</p>"/utf8>>},
        markdown:to_html(<<"$a$"/utf8>>),
        "should not support math (text) by default"
    ),
    ok.

-spec test_math_text_case_2(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_math_text_case_2(_Config) ->
    ?assertMatch(
        {ok,
            <<"<p><code class=\"language-math math-inline\">foo</code> <code class=\"language-math math-inline\">bar</code></p>"/utf8>>},
        markdown:to_html_with_options(<<"$foo$ $$bar$$"/utf8>>, ?math),
        "should support math (text) if enabled"
    ),
    ok.

