-spec test_mdx_expression_flow_case_33(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_mdx_expression_flow_case_33(_Config) ->
    ?assertMatch(
        {ok, <<""/utf8>>},
        markdown:to_html_with_options(<<"<a {...b} />"/utf8>>, ?swc),
        "should support a spread"
    ),
    ok.

-spec test_mdx_expression_flow_case_34(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_mdx_expression_flow_case_34(_Config) ->
    ?assertMatch(
        {ok,
            <<"1:5: Unexpected prop in spread (such as `{x}`): only a spread is supported (such as `{...x}`) (mdx:swc)"/utf8>>},
        markdown:to_html_with_options(<<"<a {b} />"/utf8>>, ?swc),
        "should crash if not a spread"
    ),
    ok.

-spec test_mdx_expression_flow_case_35(Config) -> markdown_test:testcase() when
    Config :: ct_suite:ct_config().
test_mdx_expression_flow_case_35(_Config) ->
    ?assertMatch(
        {ok, <<"1:13: Could not parse expression with swc: Expression expected (mdx:swc)"/utf8>>},
        markdown:to_html_with_options(<<"<a {...?} />"/utf8>>, ?swc),
        "should crash on an incorrect spread"
    ),
